```javascript
class MinesweeperGame {
  constructor(rows = 9, cols = 9, mineCount = 10) {
    this.rows = rows;
    this.cols = cols;
    this.mineCount = mineCount;
    this.board = [];
    this.revealed = 0;
    this.gameOver = false;
    this.flagCount = 0;
    this.firstClick = true;

    this.initializeBoard();
  }

  initializeBoard() {
    // ë¹ˆ ë³´ë“œ ìƒì„±
    this.board = Array(this.rows).fill().map(() => 
      Array(this.cols).fill().map(() => ({
        isMine: false,
        isRevealed: false,
        isFlagged: false,
        neighborMines: 0
      }))
    );
  }

  placeMines(firstClickRow, firstClickCol) {
    let minesPlaced = 0;

    while (minesPlaced < this.mineCount) {
      const row = Math.floor(Math.random() * this.rows);
      const col = Math.floor(Math.random() * this.cols);

      // ì²« í´ë¦­ ìœ„ì¹˜ì™€ ì£¼ë³€ 8ì¹¸ì—ëŠ” ì§€ë¢°ë¥¼ ë°°ì¹˜í•˜ì§€ ì•ŠìŒ
      const isFirstClickArea = Math.abs(row - firstClickRow) <= 1 && 
                              Math.abs(col - firstClickCol) <= 1;

      if (!this.board[row][col].isMine && !isFirstClickArea) {
        this.board[row][col].isMine = true;
        minesPlaced++;

        // ì£¼ë³€ 8ì¹¸ì˜ neighborMines ê°’ ì¦ê°€
        this.incrementNeighborMines(row, col);
      }
    }

    this.firstClick = false;
  }

  incrementNeighborMines(row, col) {
    for (let r = Math.max(0, row - 1); r <= Math.min(this.rows - 1, row + 1); r++) {
      for (let c = Math.max(0, col - 1); c <= Math.min(this.cols - 1, col + 1); c++) {
        if (r !== row || c !== col) {
          this.board[r][c].neighborMines++;
        }
      }
    }
  }

  // ê²Œì„ ë³´ë“œ í¬ê¸° ë³€ê²½ ë©”ì„œë“œ
  resizeBoard(rows, cols, mineCount) {
    this.rows = rows;
    this.cols = cols;
    this.mineCount = mineCount;
    this.reset();
  }

  // ê²Œì„ ë¦¬ì…‹
  reset() {
    this.board = [];
    this.revealed = 0;
    this.gameOver = false;
    this.flagCount = 0;
    this.firstClick = true;

    this.initializeBoard();
  }
}

// ê²Œì„ ì¸ìŠ¤í„´ìŠ¤ ìƒì„±
const game = new MinesweeperGame();
```

