class MinesweeperGame {
  // ê¸°ì¡´ ì½”ë“œ ìœ ì§€...

  // ì…€ í´ë¦­ ì²˜ë¦¬
  revealCell(row, col) {
    // ì´ë¯¸ ê²Œì„ì´ ëë‚¬ê±°ë‚˜, ì´ë¯¸ ì—´ë¦° ì…€ì´ê±°ë‚˜, ê¹ƒë°œì´ ê½‚íŒ ì…€ì´ë©´ ë¬´ì‹œ
    if (this.gameOver || this.board[row][col].isRevealed || this.board[row][col].isFlagged) {
      return;
    }

    // ì²« í´ë¦­ì¸ ê²½ìš° ì§€ë¢° ë°°ì¹˜
    if (this.firstClick) {
      this.placeMines(row, col);
    }

    // ì§€ë¢°ë¥¼ í´ë¦­í•œ ê²½ìš°
    if (this.board[row][col].isMine) {
      this.gameOver = true;
      this.revealAllMines();
      return 'gameover';
    }

    // ì…€ ì˜¤í”ˆ
    this.board[row][col].isRevealed = true;
    this.revealed++;

    // ì£¼ë³€ ì§€ë¢°ê°€ ì—†ëŠ” ë¹ˆ ì…€ì¸ ê²½ìš° ì¬ê·€ì ìœ¼ë¡œ ì£¼ë³€ ì…€ ì˜¤í”ˆ
    if (this.board[row][col].neighborMines === 0) {
      this.revealEmptyCells(row, col);
    }

    // ìŠ¹ë¦¬ ì¡°ê±´ ì²´í¬: ì§€ë¢°ê°€ ì•„ë‹Œ ëª¨ë“  ì…€ì´ ì—´ë ¸ëŠ”ì§€
    if (this.revealed === (this.rows * this.cols - this.mineCount)) {
      this.gameOver = true;
      return 'win';
    }

    return 'continue';
  }

  // ë¹ˆ ì…€ ì£¼ë³€ì„ ì¬ê·€ì ìœ¼ë¡œ ì—´ê¸°
  revealEmptyCells(row, col) {
    for (let r = Math.max(0, row - 1); r <= Math.min(this.rows - 1, row + 1); r++) {
      for (let c = Math.max(0, col - 1); c <= Math.min(this.cols - 1, col + 1); c++) {
        // ì´ë¯¸ ì—´ë¦° ì…€ì´ê±°ë‚˜ ê¹ƒë°œì´ ê½‚íŒ ì…€ì€ ë¬´ì‹œ
        if (this.board[r][c].isRevealed || this.board[r][c].isFlagged) {
          continue;
        }

        // ì…€ ì˜¤í”ˆ
        this.board[r][c].isRevealed = true;
        this.revealed++;

        // ë¹ˆ ì…€ì´ë©´ ì¬ê·€ í˜¸ì¶œ
        if (this.board[r][c].neighborMines === 0) {
          this.revealEmptyCells(r, c);
        }
      }
    }
  }

  // ê¹ƒë°œ í† ê¸€
  toggleFlag(row, col) {
    // ì´ë¯¸ ì—´ë¦° ì…€ì´ê±°ë‚˜ ê²Œì„ì´ ëë‚œ ê²½ìš° ë¬´ì‹œ
    if (this.board[row][col].isRevealed || this.gameOver) {
      return;
    }

    if (this.board[row][col].isFlagged) {
      this.board[row][col].isFlagged = false;
      this.flagCount--;
    } else {
      this.board[row][col].isFlagged = true;
      this.flagCount++;
    }

    return this.flagCount;
  }

  // ëª¨ë“  ì§€ë¢° ë³´ì—¬ì£¼ê¸° (ê²Œì„ ì˜¤ë²„ ì‹œ)
  revealAllMines() {
    for (let row = 0; row < this.rows; row++) {
      for (let col = 0; col < this.cols; col++) {
        if (this.board[row][col].isMine) {
          this.board[row][col].isRevealed = true;
        }
      }
    }
  }
}
